import streamlit as st
import pandas as pd
import joblib
import numpy as np

def map_number_to_category(number):
    if number == 0:
        return 'High'
    elif number == 1:
        return 'Low'
    elif number == 2:
        return 'Luxury'
    elif number == 3:
        return 'Medium'
    elif number == 4:
        return 'Very High'
    else:
        return None  # Untuk menangani angka yang tidak dikenali

